--Create a Master Key for encryption 

CREATE MASTER KEY  ENCRYPTION BY PASSWORD = 'Password@123';

--Create Credintial
CREATE DATABASE SCOPED CREDENTIAL SynapseCreds
WITH 
IDENTITY = 'Managed Identity';

--Create an external data source
CREATE EXTERNAL DATA SOURCE Raw_Ext_Source 
WITH
(
    LOCATION = 'https://saazsynapsepract.blob.core.windows.net/externaldatasource/inputdata',
    CREDENTIAL = SynapseCreds
);
